import { useEffect, useState } from "react";
import Filter from "./components/Filter";

const App = () => {
  const [persons, setPersons] = useState([
    { name: "Arto Hellas", number: "040-123456", id: 1 },
    { name: "Ada Lovelace", number: "39-44-5323523", id: 2 },
    { name: "Dan Abramov", number: "12-43-234345", id: 3 },
    { name: "Mary Poppendieck", number: "39-23-6423122", id: 4 },
  ]);
  const [newName, setNewName] = useState("");
  const [newNum, setNewNum] = useState("");
  const [filterQuery, setFilterQuery] = useState("");

  const addNameandNum = (e) => {
    e.preventDefault();

    if (persons.find((person) => person.name === newName)) {
      alert(`${newName} is already added to phonebook`);
    } else {
      const newPerson = { name: newName, number: newNum };
      setPersons(persons.concat(newNum));
      setNewNum("");
      setPersons(persons.concat(newPerson));
      setNewName("");
    }
  };
  const handleChange = (setValue) => (e) => {
    setValue(e.target.value);
  };

  return (
    <div>
      <h1> Phonebook</h1>
      <p>
        Filter shown with{" "}
        <input value={filterQuery} onChange={handleChange(setFilterQuery)} />
      </p>

      <h2>Add a new</h2>
      <form onSubmit={addNameandNum}>
        <div>
          name: <input value={newName} onChange={handleChange(setNewName)} />
        </div>
        <div>
          number: <input value={newNum} onChange={handleChange(setNewNum)} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>

      {persons
        .filter((person) => person.name.toLowerCase().includes(filterQuery))
        .map((persons) => (
          <div key={persons.name}>
            {persons.name}
            {persons.number}
          </div>
        ))}
    </div>
  );
};

export default App;
