import { useState } from "react";

import Filter from "./components/Filter";
// import PersonForm from "./components/PersonForm";
import Persons from "./components/Persons";

const App = () => {
  const [persons, setPersons] = useState([
    { name: "Arto Hellas", number: "040-123456", id: 1 },
    { name: "Ada Lovelace", number: "39-44-5323523", id: 2 },
    { name: "Dan Abramov", number: "12-43-234345", id: 3 },
    { name: "Mary Poppendieck", number: "39-23-6423122", id: 4 },
  ]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [filterQuery, setFilterQuery] = useState("");

  const handleAddNewPerson = (e) => {
    e.preventDefault();

    if (persons.find((person) => person.name === newName)) {
      alert(`${newName} is already added to phonebook`);
      console.log("heelo");
    } else {
      const newPerson = { name: newName, number: newNumber };

      setPersons(persons.concat(newPerson));

      setNewName = "";
    }
  };
  const handleChange = (setValue) => (e) => {
    setValue(e.target.value);
  };

  return (
    <div>
      <h1> Phonebook</h1>
      <Filter query={filterQuery} handleChange={handleChange(setFilterQuery)} />

      <h2>Add a new</h2>
      {/* <PersonForm
        name={newName}
        number={newNumber}
        handleChangeName={handleChange(setNewName)}
        handleChangeNumber={handleChange(setNewNumber)}
        handleAddPerson={handleAddNewPerson}
      /> */}
      <form onSubmit={handleAddNewPerson}>
        <div>
          name: <input value={newName} onChange={handleChange(setNewName)} />
        </div>
        <div>
          number:{" "}
          <input value={newNumber} onChange={handleChange(setNewNumber)} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>

      <h2>Numbers</h2>
      <Persons persons={persons} query={filterQuery} />
    </div>
  );
};

export default App;
