import { useEffect, useState } from "react";

const App = () => {
  const [persons, setPersons] = useState([{ name: "Arto Hellas" }]);
  const [newName, setNewName] = useState("");
  const [perNum, setPerNum] = useState([{ num: "040-1234567" }]);
  const [newNum, setNewNum] = useState("");

  const addNameandNum = (e) => {
    e.preventDefault();

    if (persons.find((person) => person.name === newName)) {
      alert(`${newName} is already added to phonebook`);
    } else {
      const newPerson = { name: newName, num: newNum };
      setPerNum(perNum.concat(newNum));
      setNewNum("");
      setPersons(persons.concat(newPerson));
      setNewName("");
    }
  };
  const handleNameChange = (event) => {
    setNewName(event.target.value);
    setNewNum(event.target.value);
  };

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addNameandNum}>
        <div>
          name: <input value={newName} onChange={handleNameChange} />
          num: <input value={newNum} onChange={handleNameChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>

      {persons.map((persons) => (
        <div key={persons.name}>{persons.name}</div>
      ))}
    </div>
  );
};

export default App;
