import { useState, useEffect } from "react";
import Filter from "./components/Filter";
import PersonForm from "./components/PersonForm";
import Persons from "./components/Persons";
import noteService from './services/notes'
import Notification from "./components/Notification";
const App = () => {
  const [persons, setPersons] = useState([
  ]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [filterQuery, setFilterQuery] = useState("");
  const [status ,setStatus] = useState(null)
  const [message, setMessage] = useState(null)


  useEffect(() => {
    noteService
      .getAll()
      .then(response => {
        setPersons(response.data)
      })
  }, [])

  const handleAddNewPerson = (e) => {
    e.preventDefault();
    const newPerson = { name: newName, number: newNumber };
    const fPerson = persons.find((person) => person.name === newName)

    if (fPerson) {
      if(
        window.confirm(
          `${newName} is alrerady added to phonebook, repllace the old number with a new one?`
        )

      ) {
        noteService.update(fPerson.id,newPerson).then(returnedPerson => {
          setPersons(persons.map(person =>
             (person.id !== fPerson.id ? person : returnedPerson)
             )
             )
        }).catch( error => {
          setStatus('error')
          setMessage(`Information of ${fPerson.name} has already been removed from server`)
          setTimeout(() => {
            setStatus(null)
            setMessage(null)
          }, 5000)

          setPersons(persons.filter(person => person.id !== fPerson.id))
        })
      } 
    }
    else  {
      noteService
      .create (newPerson)
      .then(addedPerson => {
        setPersons(persons.concat(addedPerson.data))
        setStatus ('success')
        setMessage(
          `Added ${addedPerson.data.name}`
        )
        setTimeout(() => {
          setStatus(null)
          setMessage(null)
        }, 5000)
        setNewName('')
        setNewNumber('')
      })
    }
  };


  const handleChange = (setValue) => (e) => {
    setValue(e.target.value);
  };

  const handleRemovePerson =( id,name) => () => {
    if (window.confirm(`Delete ${name}`)) {
      noteService.remove(id).then(() => {
        setPersons(persons.filter(person => person.name !== name))
      })
    }
  }

  return (
    <div>

      <h1> Phonebook</h1>
      <Notification message ={message} status = {status} />
      <Filter query={filterQuery} handleChange={handleChange(setFilterQuery)} />

      <h2>Add a new</h2>
      <PersonForm
        name={newName}
        number={newNumber}
        handleChangeName={handleChange(setNewName)}
        handleChangeNumber={handleChange(setNewNumber)}
        handleAddPerson={handleAddNewPerson}
      />

      <h2>Numbers</h2>
      <Persons persons={persons} query={filterQuery} />
      handleRemovePerson= {handleRemovePerson}

    </div>
  );
};

export default App;
